import { useState, useEffect } from "react";
import WebFont from "webfontloader";

import axios from 'axios';

import TheMain from "./Pages/TheMain";

import { createTheme } from "@mui/material/styles";
import { ThemeProvider } from "@mui/material/styles";
import { cyan } from "@mui/material/colors";
import { Typography } from "@mui/material";

const theme = createTheme({
  typography: {
    fontFamily: "'Comfortaa', cursive",
    fontSize: 14
  },
  palette: {
    primary: cyan,
    secondary: cyan
  },
  components: {
    MuiPaper: {
      styleOverrides: {
        root: {}
      }
    },
    MuiButton: {
      styleOverrides: {
        root: {
          width: "3em",
          height: "3em",
          fontSize: "1em",
          marginBottom: "0.5em"
        }
      }
    },
    MuiCard: {
      styleOverrides: {
        root: {
          backgroundColor: cyan[50]
        }
      }
    }
  }
});


export default function App() {
  const [response, setResponse] = useState('');
  const [axiostext, setText] = useState('');
  useEffect(() => {
    WebFont.load({
      google: {
        families: ["Comfortaa"]
      }
    });
  });

  useEffect(()=>{callApi('/api/mensagem')
      .then(res => setResponse(res.express))
      .catch(err => console.log(err));
  });

  useEffect(()=>{
    axios.get('/api/mensagem')
      .then(res => setText(res.data))
      .catch(err => {setText(err);console.error(err)})
  });

  const callApi = async (api, data=undefined) => {
    const response = await (data ? fetch(api, data) : fetch(api));
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);

    return body;
  };

  return (
    <ThemeProvider theme={theme}>
      <Typography>
        {response}{'\n'}
        {axiostext}
      </Typography>
      <TheMain />
    </ThemeProvider>
  );
}
